import React from 'react';

export default class Clients extends React.Component {
  constructor() {
    super();
    this.state = {
      name : '',
      email : ''
    };
  }

  handleChange = (event) => {
    const target = event.target;
    const name = target.name;
    this.setState({
      [name]: target.value
    });
    alert(name)
  }

  handleSubmit = (event) => {
    alert('Ваш любимый вкус: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <section class="project_need">
				<div class="project_wrapper">
					<h1>Need A Project?</h1>
					<p>Let us know what you're looking for in an agency. We'll take a look and see
if this could be the start of something beautiful.</p>
					<form onSubmit={this.handleSubmit} action="#" name="newform" method="post">
						<div class="form_inputs">
							<input type="text" placeholder="Your Name" value={this.state.name} onChange={this.handleChange} />
							<input type="text" placeholder="Your Email" value={this.state.email} onChange={this.handleChange}/>
						</div>
						<input class="input_title" type="text" placeholder="Your Title" />
						<textarea name="text" placeholder="Your Comment"></textarea>
						<div class="button_block">
							<a href="#">SEND MESSAGE</a>
						</div>
					</form>
				</div>
			</section>
    );
  }
}
